import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular } from '@fortawesome/fontawesome-svg-core/import.macro';

const Ratings = ({ratings, numOfReview, variant}) => {

    const solidStar = <FontAwesomeIcon icon={solid('star')} />;
    const blankStar = <FontAwesomeIcon icon={regular('star')} />;
    const halfStar = <FontAwesomeIcon icon={solid('star-half-stroke')} />;

    const dummyStar = [1,2,3,4,5];
    //3.8
    const rating = dummyStar.map((star, i) => {
        if(star <= ratings){
            return solidStar
        } else {
            if(ratings % 1 === 0 && (star - ratings.toFixed(0)) < .5) {
                return blankStar
            } else {
                return halfStar
            }
            //return star - ratings  ? blankStar : (ratings % 1 === 0 ? blankStar : halfStar)
        }
    })
    console.log(false && false)
    return ( <div>
        {rating} - {numOfReview} reviews
    </div> );
}
 
export default Ratings;
