import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular } from '@fortawesome/fontawesome-svg-core/import.macro';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import Ratings from '../../components/Ratings/Ratings';
import products from '../../products';
const PDP = () => {
    const params = useParams();
    const product = products.filter((a) => a.id === Number(params.productId));
    const {category, id, name, description, specs, images, imagePreview, ratings, numOfReviews, stocks, listPrice, price} = product[0];
    const stocksOption = () => {
        if(stocks > 0 && stocks < 10){
            return (<Form.Select>
                {[...Array(stocks).keys()].map((a) => <option key={a}>{a}</option>)}
            </Form.Select>)
        } else if(stocks > 10){
            return (<Form.Select>
                {[...Array(10).keys()].map(a => <option key={a}>{a}</option>)}
            </Form.Select>)
        } else {
            return <Form.Label>Out of stock</Form.Label>
        }
    }
    console.log(stocksOption())
    return (
        <div className="pdp">
            <section className="breadcrumbs">
                <Container>
                    <Link to="/">Home</Link>
                    /
                    <Link to="/">{category.charAt(0).toUpperCase() + category.slice(1)}</Link>
                    /
                    <Link to="#">{id}</Link>
                </Container>
            </section>
            <section className="upper-pdp">
                <Container>
                    <Row>
                        <Col>
                        
                        </Col>
                        <Col>
                            <h1>
                                {name}
                            </h1>
                            <Ratings ratings={ratings} numOfReview={numOfReviews} />
                            <div className="share-and-wishlist py-2">
                                <FontAwesomeIcon icon={solid('share-nodes')} />
                                <FontAwesomeIcon icon={regular('heart')} />
                            </div>
                            <div className="product-prices d-flex align-items-center justify-content-between">
                                <div className="listprice-and-save">
                                    <del>P{listPrice}</del><br />
                                    <strong>Save {listPrice - price}</strong>
                                </div>
                                <h3>
                                    {price}
                                </h3>
                            </div>
                            <Form>
                                <Form.Group>
                                    <Form.Select>
                                        {stocksOption()}
                                    </Form.Select>
                                </Form.Group>
                                <Form.Group>
                                    <Button variant="primary">Buy Now</Button>
                                    <Button variant="secondary">Add To Cart</Button>
                                </Form.Group>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </section>
            PDP
        </div>
    );
}
 
export default PDP;
