import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { solid, regular } from '@fortawesome/fontawesome-svg-core/import.macro';
import { Container, Row, Col } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import Ratings from '../../components/Ratings/Ratings';
import products from '../../products';
const PDP = () => {
    const params = useParams();
    const product = products.filter((a) => a.id === Number(params.productId));
    const {category, id, name, description, specs, images, imagePreview, ratings, numOfReviews, stocks, listPrice, price} = product[0];
    console.log(category)
    console.log(params.productId)
    return (
        <div className="pdp">
            <section className="breadcrumbs">
                <Container>
                    <Link to="/">Home</Link>
                    /
                    <Link to="/">{category.charAt(0).toUpperCase() + category.slice(1)}</Link>
                    /
                    <Link to="#">{id}</Link>
                </Container>
            </section>
            <section className="upper-pdp">
                <Container>
                    <Row>
                        <Col>
                        
                        </Col>
                        <Col>
                            <h1>
                                {name}
                            </h1>
                            <Ratings ratings={ratings} numOfReview={numOfReviews} />
                            <div className="share-and-wishlist">
                                <FontAwesomeIcon icon={regular('share-nodes')} />
                                <FontAwesomeIcon icon={regular('heart')} />
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>
            PDP
        </div>
    );
}
 
export default PDP;
